SELECT DEBUGINFO
  FROM SYS.ALL_PROBE_OBJECTS PO
 WHERE PO.OWNER = 'DBO'
   AND PO.OBJECT_NAME = 'DAY'
   AND PO.OBJECT_TYPE = 'FUNCTION';



