view sourceprint 
001 GRANT USAGE ON *.* TO 'discuz'@'localhost' IDENTIFIED BY PASSWORD '*C242DDD213BE9C6F8DA28D49245BF69FC79A86EB';

002 GRANT ALL PRIVILEGES ON `discuz`.* TO 'discuz'@'localhost';

